<?php

namespace Lib\Tests\Helper {

    use App\Dto\Onboard\OnboardResponse;
    use Exception;

    /**
     * Read onboard responses from file located in the test folder.
     * @package Lib\Tests\Helper
     */
    class OnboardResponseRepository
    {
        /**
         * Read and parse a onboard response from the storage.
         * @param string $identifier
         * @return OnboardResponse
         * @throws Exception
         */
        public static function read(string $identifier): OnboardResponse
        {
            $data = match ($identifier) {
                Identifier::COMMUNICATION_UNIT_HTTP => Content::COMMUNICATION_UNIT_HTTP,
                Identifier::COMMUNICATION_UNIT_MQTT => Content::COMMUNICATION_UNIT_MQTT,
                Identifier::FARMING_SOFTWARE_MQTT => Content::FARMING_SOFTWARE_MQTT,
                Identifier::TELEMETRY_PLATFORM_MQTT => Content::TELEMETRY_PLATFORM_MQTT,
                Identifier::ROUTING_DEVICE_MQTT => Content::ROUTING_DEVICE_MQTT,
                default => throw new Exception("Could not read onboard response."),
            };
            $onboardResponse = new OnboardResponse();
            return $onboardResponse->jsonDeserialize($data);
        }
    }

